    this.getProducts();
  },

  methods: {
    async getProducts() {
      try {
        const response = await axios.get("ase_good_by/getListGoodby");
        this.products = response.data;
        console.log(this.products);
      } catch (error) {
        console.log(error);
      }
    },

    async deleteProduct(id) {
      try {
        await axios.delete(`product/${id}`);
        this.getProducts();
      } catch (error) {
        console.log(error);
      }
    }

  }
};
