import Vue from 'vue'
import axios from 'axios'

const guest = axios.create({
	baseURL :'http://localhost/atm/public/', 
	headers : {
		"Content-type": "application/x-www-form-urlencoded",
		"x-Requested-with" : "XMLHttpRequest"
	},
});


Vue.prototype.$guest = guest;
export{guest};
